# –°–Ω–∞—á–∞–ª–∞ —É—Å—Ç–∞–Ω–æ–≤–∏: pip install deepmultilingualpunctuation

from deepmultilingualpunctuation import PunctuationModel
import time


def test_punctuation(text):
    """–¢–µ—Å—Ç–∏—Ä—É–µ—Ç –¥–æ–±–∞–≤–ª–µ–Ω–∏–µ –ø—É–Ω–∫—Ç—É–∞—Ü–∏–∏ –∫ —Ç–µ–∫—Å—Ç—É"""

    print("=" * 60)
    print("–¢–ï–°–¢ DEEPMULTILINGUALPUNCTUATION")
    print("=" * 60)

    # –ó–∞–≥—Ä—É–∂–∞–µ–º –º–æ–¥–µ–ª—å
    print("–ó–∞–≥—Ä—É–∂–∞–µ–º –º–æ–¥–µ–ª—å...")
    start_time = time.time()

    try:
        model = PunctuationModel()
        load_time = time.time() - start_time
        print(f"‚úÖ –ú–æ–¥–µ–ª—å –∑–∞–≥—Ä—É–∂–µ–Ω–∞ –∑–∞ {load_time:.2f} —Å–µ–∫—É–Ω–¥")
    except Exception as e:
        print(f"‚ùå –û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –º–æ–¥–µ–ª–∏: {e}")
        return

    # –û–±—Ä–∞–±–∞—Ç—ã–≤–∞–µ–º —Ç–µ–∫—Å—Ç
    print(f"\n–ò—Å—Ö–æ–¥–Ω—ã–π —Ç–µ–∫—Å—Ç ({len(text.split())} —Å–ª–æ–≤):")
    print("-" * 40)
    print(text[:200] + "..." if len(text) > 200 else text)

    print(f"\n–û–±—Ä–∞–±–∞—Ç—ã–≤–∞–µ–º...")
    start_time = time.time()

    try:
        result = model.restore_punctuation(text)
        process_time = time.time() - start_time
        print(f"‚úÖ –û–±—Ä–∞–±–æ—Ç–∞–Ω–æ –∑–∞ {process_time:.2f} —Å–µ–∫—É–Ω–¥")
    except Exception as e:
        print(f"‚ùå –û—à–∏–±–∫–∞ –æ–±—Ä–∞–±–æ—Ç–∫–∏: {e}")
        return

    print(f"\n–†–µ–∑—É–ª—å—Ç–∞—Ç —Å –ø—É–Ω–∫—Ç—É–∞—Ü–∏–µ–π:")
    print("-" * 40)
    print(result)

    # –°—Ç–∞—Ç–∏—Å—Ç–∏–∫–∞
    print(f"\nüìä –°–¢–ê–¢–ò–°–¢–ò–ö–ê:")
    print(f"–ò—Å—Ö–æ–¥–Ω–∞—è –¥–ª–∏–Ω–∞: {len(text)} —Å–∏–º–≤–æ–ª–æ–≤, {len(text.split())} —Å–ª–æ–≤")
    print(f"–†–µ–∑—É–ª—å—Ç–∞—Ç: {len(result)} —Å–∏–º–≤–æ–ª–æ–≤, {len(result.split())} —Å–ª–æ–≤")
    print(f"–í—Ä–µ–º—è –æ–±—Ä–∞–±–æ—Ç–∫–∏: {process_time:.2f} —Å–µ–∫")
    print(f"–°–∫–æ—Ä–æ—Å—Ç—å: {len(text.split()) / process_time:.1f} —Å–ª–æ–≤/—Å–µ–∫")

    return result


if __name__ == "__main__":
    # –ü—Ä–∏–º–µ—Ä —Ç–µ–∫—Å—Ç–∞ –±–µ–∑ –ø—É–Ω–∫—Ç—É–∞—Ü–∏–∏
    sample_text = """
    –ø—Ä–∏–≤–µ—Ç –º–µ–Ω—è –∑–æ–≤—É—Ç –∏–≤–∞–Ω —è —Ä–∞–±–æ—Ç–∞—é –≤ –∫–æ–º–ø–∞–Ω–∏–∏ –∫–æ—Ç–æ—Ä–∞—è –∑–∞–Ω–∏–º–∞–µ—Ç—Å—è —Ä–∞–∑—Ä–∞–±–æ—Ç–∫–æ–π –ø—Ä–æ–≥—Ä–∞–º–º–Ω–æ–≥–æ –æ–±–µ—Å–ø–µ—á–µ–Ω–∏—è 
    —Å–µ–≥–æ–¥–Ω—è —è —Ö–æ—á—É —Ä–∞—Å—Å–∫–∞–∑–∞—Ç—å –≤–∞–º –æ –Ω–∞—à–µ–º –Ω–æ–≤–æ–º –ø—Ä–æ–µ–∫—Ç–µ —ç—Ç–æ –æ—á–µ–Ω—å –∏–Ω—Ç–µ—Ä–µ—Å–Ω–∞—è —Å–∏—Å—Ç–µ–º–∞ –∫–æ—Ç–æ—Ä–∞—è –ø–æ–º–æ–∂–µ—Ç 
    –∞–≤—Ç–æ–º–∞—Ç–∏–∑–∏—Ä–æ–≤–∞—Ç—å –º–Ω–æ–≥–∏–µ –ø—Ä–æ—Ü–µ—Å—Å—ã –≤ —Ä–∞–±–æ—Ç–µ —Å –∫–ª–∏–µ–Ω—Ç–∞–º–∏ –∫–∞–∫ –≤—ã –¥—É–º–∞–µ—Ç–µ —ç—Ç–æ –±—É–¥–µ—Ç –ø–æ–ª–µ–∑–Ω–æ –¥–ª—è –±–∏–∑–Ω–µ—Å–∞
    """

    print("–¢–ï–°–¢–û–í–´–ô –ó–ê–ü–£–°–ö:")
    test_punctuation(sample_text.strip())

    print("\n" + "=" * 60)
    print("–í–°–¢–ê–í–¨ –°–í–û–ô –¢–ï–ö–°–¢ –ù–ò–ñ–ï:")
    print("=" * 60)

    # –ó–¥–µ—Å—å –≤—Å—Ç–∞–≤—å —Å–≤–æ–π —Ç–µ–∫—Å—Ç –¥–ª—è —Ç–µ—Å—Ç–∏—Ä–æ–≤–∞–Ω–∏—è
    your_text = """
     —á—Ç–æ –∂–µ –¥–µ–ª–∞—Ç—å –ø–æ—Å–ª–µ –ø—Ä–æ—Ö–æ–∂–¥–µ–Ω–∏—è –±–∞–∑–æ–≤—ã—Ö –º–æ–¥—É–ª–µ–π –ø–æ—Å–ª–µ –ø—Ä–æ—Ö–æ–∂–¥–µ–Ω–∏—è bootcamp –∞ –≤–æ-–ø–µ—Ä–≤—ã—Ö –º–æ–∂–Ω–æ –µ—â–µ —Ä–∞–∑ –≤—Å–µ –ø–µ—Ä–µ—Å–º–æ—Ç—Ä–µ—Ç—å –ø–æ—Ç–æ–º—É —á—Ç–æ —Å–∫–æ—Ä–µ–µ –≤—Å–µ–≥–æ –Ω–µ —É –≤—Å–µ—Ö —Å—Ä–∞–∑—É –≤—Å–µ –∑–∞–∫—Ä–µ–ø–∏–ª–∞—Å—å —Ç–æ —á—Ç–æ –¥–æ–ª–∂–Ω–æ –∑–∞–∫—Ä–µ–ø–∏—Ç—å—Å—è –Ω–∞ –ø—Ä–∞–∫—Ç–∏–∫–µ –≤—Å–µ —Å–¥–µ–ª–∞—Ç—å —Ç–æ –æ —á–µ–º —è —Ä–∞—Å—Å–∫–∞–∑—ã–≤–∞–ª –≤–æ –≤—Å–µ—Ö —É—Ä–æ–∫–∞—Ö –∏—Å—Å–ª–µ–¥–æ–≤–∞—Ç—å —Ç–æ–π —Å—Ç—Ä–∞—Ç–µ–≥–∏–∏ –∫–æ—Ç–æ—Ä—É—é —è –¥–∞–ª –≤ –æ–¥–Ω–æ–º –∏–∑ –ø—Ä–æ—à–ª—ã—Ö —É—Ä–æ–∫–æ–≤ —Ä–µ–≥—É–ª—è—Ä–Ω–æ—Å—Ç–∏ –∏–Ω–≤–µ—Å—Ç–∏—Ü–∏–π –ø–æ—Å—Ç–æ—è–Ω—Å—Ç–≤–æ —ç—Ç–æ –∫–ª—é—á –∫ —É—Å–ø–µ—Ö—É –≤—Å–µ —á—Ç–æ —Å–≤–µ—Ä—Ö—É –¥–µ—Ñ–∞–π –∫–∞–∫–∏–µ-—Ç–æ —Å—Ç—Ä–∞—Ç–µ–≥–∏–∏ —ç—Ç–æ –ø—Ä–æ—Å—Ç–æ –ø—Ä–∏—è—Ç–Ω–æ–µ –¥–æ–ø–æ–ª–Ω–µ–Ω–∏–µ –∫–æ—Ç–æ—Ä–æ–µ —É—Å–∫–æ—Ä—è–µ—Ç —Ä–æ—Å—Ç –ø–æ—Ä—Ç—Ñ–µ–ª—è —É—Å–∫–æ—Ä—è–µ—Ç —Ä–∞–∑–≤–∏—Ç–∏–µ –∏–Ω–≤–µ—Å—Ç–∏—Ü–∏–æ–Ω–Ω–æ–≥–æ –ø–æ—Ä—Ç—Ñ–µ–ª—è —É—Å–∫–æ—Ä—è–µ—Ç –¥–æ—Ö–æ–¥–Ω–æ—Å—Ç—å —É–≤–µ–ª–∏—á–∏–≤–∞–µ—Ç –¥–∞ —Ç–æ –µ—Å—Ç—å —ç—Ç–æ –≤—Å–µ —É–∂–µ –Ω–∞—Å—Ç—Ä–æ–π–∫–∞ –Ω–∞–¥ –ø—Ä–æ—á–Ω–æ –∑–∞–ª–æ–∂–µ–Ω–Ω—ã–º —Ñ—É–Ω–¥–∞–º–µ–Ω—Ç–æ–º —è –Ω–∞–¥–µ—é—Å—å —á—Ç–æ –≤–æ—Ç —ç—Ç–∏ –±–∞–∑–æ–≤—ã–µ –º–æ–¥—É–ª–∏ –∑–∞–ª–æ–∂–∏–ª–∏ —Ñ—É–Ω–¥–∞–º–µ–Ω—Ç –∏–Ω–≤–µ—Å—Ç–∏—Ü–∏–æ–Ω–Ω—ã–π —Ñ—É–Ω–¥–∞–º–µ–Ω—Ç –¥–ª—è –ø–æ–Ω–∏–º–∞–Ω–∏—è —Ç–æ–≥–æ —á—Ç–æ –ø—Ä–æ–∏—Å—Ö–æ–¥–∏—Ç –≤ –¥–µ—Ñ–∞–π —Ö–æ—Ç—è –±—ã –Ω–∞ –±–∞–∑–æ–≤–æ–º —É—Ä–æ–≤–Ω–µ —ç—Ç–æ–≥–æ —É–∂–µ –¥–æ—Å—Ç–∞—Ç–æ—á–Ω–æ –¥–ª—è —Ç–æ–≥–æ —á—Ç–æ–±—ã —Ä–∞–∑–≤–∏–≤–∞—Ç—å—Å—è –∏ –∑–∞—Ä–∞–±–∞—Ç—ã–≤–∞—Ç—å –∫—Ä–∏–ø—Ç–µ –µ—Å–ª–∏ —Ç—ã –ø—Ä—è–º–æ —Å–µ–π—á–∞—Å –Ω–∞—Ö–æ–¥–∏—à—å—Å—è –Ω–∞ –ø—Ä–æ–≥—Ä–∞–º–º–µ –≤–µ–± 3 bootcamp –∏ —Ç—ã –ø–æ–Ω–∏–º–∞–µ—à—å —á—Ç–æ —Ö–æ—á–µ—à—å —Ä–∞–∑–≤–∏–≤–∞—Ç—å—Å—è –¥–∞–ª—å—à–µ —Ö–æ—á–µ—à—å –ø—Ä–æ–±–æ–≤–∞—Ç—å —Ä–∞–∑–Ω—ã–µ –±–æ–ª–µ–µ –≤—ã—Å–æ–∫–æ–¥–æ—Ö–æ–¥–Ω—ã–µ –¥–µ—Ñ–∞–π —Å—Ç—Ä–∞—Ç–µ–≥–∏–∏ —Ö–æ—á–µ—à—å –Ω–∞ –º–∞–∫—Å–∏–º—É–º –∏—Å–ø–æ–ª—å–∑–æ–≤–∞—Ç—å —Å–≤–æ–π –∫–∞–ø–∏—Ç–∞–ª –≤ –¥–µ—Ñ–∞–π—Å –ø–æ–º–æ—â—å—é —Ä–∞–∑–ª–∏—á–Ω—ã—Ö –¥–µ—Ñ–∞–π –∏–Ω—Å—Ç—Ä—É–º–µ–Ω—Ç–æ–≤ —è –¥—É–º–∞—é —Ç—ã –ø–æ—Å–ª–µ —ç—Ç–æ–≥–æ –º–æ–¥—É–ª—è –ø–æ–Ω—è–ª —á—Ç–æ –¥–µ—Ñ–∞–π –º–æ–∂–µ—Ç –¥–µ–π—Å—Ç–≤–∏—Ç–µ–ª—å–Ω–æ –æ—á–µ–Ω—å –º–Ω–æ–≥–æ –≤—Å–µ–≥–æ –ø—Ä–µ–¥–ª–æ–∂–∏—Ç—å —Ç–æ –ø–æ–¥—É–º–∞–π –∏ —Ä–∞—Å—Å–º–æ—Ç—Ä–∏ –æ–±—É—á–µ–Ω–∏–µ –≤ –æ—Å–Ω–æ–≤–Ω–æ–π –ø—Ä–æ–≥—Ä–∞–º–º–µ –∞–∫–∞–¥–µ–º–∏–∏ —Ç–æ –µ—Å—Ç—å —ç—Ç–æ –ø—Ä–æ –º–æ–¥—É–ª–∏ —ç—Ç–æ –∑–∞–∫—Ä—ã—Ç–æ–µ —Å–æ–æ–±—â–µ—Å—Ç–≤–æ —É–∂–µ –±–æ–ª–µ–µ –Ω–∞ –±–æ–ª–µ–µ –≤—ã—Å–æ–∫–æ–º —É—Ä–æ–≤–Ω–µ –≥–æ—Ä–∞–∑–¥–æ –±–æ–ª—å—à–µ –º–∞—Ç–µ—Ä–∏–∞–ª–æ–≤ –≥–æ—Ä–∞–∑–¥–æ –±–æ–ª—å—à–µ —Å—Ç—Ä–∞—Ç–µ–≥–∏–π –±–æ–ª—å—à–µ –æ–ø—Ü–∏–π –ø–æ–¥—É–º–∞–π –Ω—É–∂–Ω–æ –ª–∏ —Ç–µ–±–µ —ç—Ç–æ –≤–æ–æ–±—â–µ –µ—Å–ª–∏ –Ω—É–∂–Ω–æ —Ç–æ –¥–æ–±—Ä–æ –ø–æ–∂–∞–ª–æ–≤–∞—Ç—å –≤ –≤–µ–∫—Ç–æ—Ä–µ academy –æ–ø—è—Ç—å –∂–µ –∑–∞—è–≤–∫—É –Ω–∞ –æ–±—É—á–µ–Ω–∏–µ —Ç—ã –º–æ–∂–µ—à—å –æ—Å—Ç–∞–≤–∏—Ç—å –ø–æ —Å—Å—ã–ª–∫–µ –≤ –æ–ø–∏—Å–∞–Ω–∏–∏ –µ—Å—Ç—å –∞–∫–∫–∞—É–Ω—Ç –ø–æ–¥–¥–µ—Ä–∂–∫–∏ –º–æ–∂–µ—à—å –Ω–∞–ø–∏—Å–∞—Ç—å —Ç—É–¥–∞ –∏ —Ç–µ–±–µ —Ä–∞—Å—Å–∫–∞–∂—É—Ç –æ —Ç–æ–º –∫–∞–∫ —Å–¥–µ–ª–∞—Ç—å –∞–ø–≥—Ä–µ–π—Ç –µ–≥–æ —Ç–∞—Ä–∏—Ñ–∞ –Ω–∞ —Ç–æ —á—Ç–æ–±—ã –≤–æ–π—Ç–∏ –≤ –ø–æ–ª–Ω–æ—Ü–µ–Ω–Ω—É—é –æ–±—É—á–∞—é—â—É—é –ø—Ä–æ–≥—Ä–∞–º–º—É –≤–µ–± 3 –¢–æ—Ç –ø–æ—Ä—Ç—Ñ–µ–ª—å, –∫–æ—Ç–æ—Ä—ã–π –ø—Ä—è–º–æ —Å–µ–π—á–∞—Å —É —Ç–µ–±—è —Å–æ–±—Ä–∞–Ω, –µ—Å–ª–∏ —Ç—ã —Å–ª–µ–¥–æ–≤–∞–ª —Ä–µ–∫–æ–º–µ–Ω–¥–∞—Ü–∏—è–º, –∫–æ—Ç–æ—Ä—ã–µ —è –¥–∞–ª –≤ –ø—Ä–æ—à–ª—ã—Ö —É—Ä–æ–∫–∞—Ö, –æ–Ω –±—É–¥–µ—Ç –ø–æ–∫–∞–∑—ã–≤–∞—Ç—å —Å–µ–±—è –æ—á–µ–Ω—å —Ö–æ—Ä–æ—à–æ –Ω–∞ –ª—é–±–æ–π —Å—Ç–∞–¥–∏–∏ —Ä—ã–Ω–∫–∞. –û–ø—è—Ç—å –∂–µ, –∫–ª—é—á–µ–≤—ã–º –º–æ–º–µ–Ω—Ç–æ–º –¥–ª—è —É—Å–ø–µ—Ö–∞ —ç—Ç–æ–≥–æ –ø–æ—Ä—Ç—Ñ–µ–ª—è —Ä–µ–≥—É–ª—è—Ä–Ω—ã–µ –∏–Ω–≤–µ—Å—Ç–∏—Ü–∏–∏. –ù–µ –ø–æ–ø—ã—Ç–∫–∞ —Ç–∞–º —á—Ç–æ-—Ç–æ —É–≥–∞–¥–∞—Ç—å. –ú—ã —ç—Ç–æ–≥–æ —Å–¥–µ–ª–∞—Ç—å –Ω–µ –º–æ–∂–µ–º. –ú—ã –æ—Å–Ω–æ–≤—ã–≤–∞–µ–º—Å—è –Ω–∞ –¥–æ–ª–≥–æ—Å—Ä–æ—á–Ω–æ–º –∏–Ω–≤–µ—Å—Ç–∏—Ü–∏–æ–Ω–Ω–æ–º –ø–æ–¥—Ö–æ–¥–µ.
    """

    if your_text.strip() and "–í–°–¢–ê–í–¨ –°–Æ–î–ê" not in your_text:
        test_punctuation(your_text.strip())
    else:
        print("üëÜ –ó–∞–º–µ–Ω–∏ your_text –Ω–∞ —Å–≤–æ–π —Ç–µ–∫—Å—Ç –∏ –∑–∞–ø—É—Å—Ç–∏ —Å–Ω–æ–≤–∞")